import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';
import CardBootstrap from 'react-bootstrap/Card';
import Image from "../../assets/cain.png";


export default function Card ({pers}){
    return(
    <>
         { /* esto es el HTML */}
        <CardContainer>
            {/* valor de hijo */}
                <CardBootstrap style={{ width: '100%' }}>
                <CardBootstrap.Img variant="top" src={Image}/>
                <CardBootstrap.Body>
                    <CardBootstrap.Title> <CardTitle nombre={pers.nombre} /></CardBootstrap.Title>
                        <CardBootstrap.Text>
                            <h4>Amigos</h4>
                            <div >
                                <Amigos amigos={pers.amigos}/>
                            </div>
                        </CardBootstrap.Text>
                    <Button variant="primary">Go somewhere</Button>
                </CardBootstrap.Body>
            </CardBootstrap>



            {/* valor de hijo */}
        </CardContainer>

    </>
    )
}
